css:
.print-format {
	margin-bottom: 18.5mm;
	margin-top: 15mm;
	margin-left: -10mm !important;
	margin-right: 10mm;
}
#motiv {
	float: left;
}
#motiv_img {
	width: 115mm;
	height: 55mm;
}
#meier_or {
	float: right;
}
#meier_or_img {
	width: 65mm;
	height: 55mm;
}
#kopf {
min-height: 70mm;
}
#gutschein_box {
width: 100%;
}
#wert {
margin-left: 25%;
margin-right: 25%;
text-align: center;
border: 1px solid red;
}
#text_to {
width: 100%;
text-align: center;
}
#barcode {
width: 100%;
text_align: center;
}
#widmung {
width: 100%;
text-align: center;
}
#beisatz{
width: 100%;
text-align: center;
}

html:
{% if doc.motiv == 'Herz' %}
	{% set motiv = '/assets/gcm/images/herz.JPG' %}
{% endif %}
{% if doc.motiv == 'Blumen' %}
	{% set motiv = '/assets/gcm/images/Blumen.JPG' %}
{% endif %}
{% if doc.motiv == 'Blumen 2' %}
	{% set motiv = '/assets/gcm/images/Blumen_2.jpg' %}
{% endif %}
{% if doc.motiv == 'Hortensien' %}
	{% set motiv = '/assets/gcm/images/Hortensien.jpg' %}
{% endif %}
{% if doc.motiv == 'Tulpenstrauss' %}
	{% set motiv = '/assets/gcm/images/tulpenstrauss.jpg' %}
{% endif %}
<div id="kopf">
<div id="motiv">
	<img id="motiv_img" src="{{ motiv }}">
</div>
<div id="meier_or">
	<img id="meier_or_img" src="/assets/gcm/images/Blumen.JPG">
</div>
</div>
<div id="gutschein_box">
<div id="wert">
Fr. {{ doc.amount}}.-
</div>
<div id="text_to">
{% if doc.text_to %}Für {{ doc.text_to }} {% endif %}
</div>
</div>
<div id="widmung">
{% if doc.inscription %}{{ doc.inscription }}{% endif %}{% if doc.text_from %}<br><br>Von {{ doc.text_from }}{% endif %}
</div>
<div id="beisatz">
<p>Dieser Gutschein ist einlösbar im<br>Garten-Center Meier, 8635 Dürnten bis zum {{ doc.valid_date }}</p>
</div>
<div id="barcode">
{{ doc.barcode }}
</div>
