css:
.print-format {
	margin-top: 12mm;
}
#gutschein_box {
	width: 70%;
	background-image: url('/assets/gcm/images/Gutschein_Fenster_leer.png');
	background-repeat: no-repeat;
	margin-left: 15%;
	margin-right: 15%;
	height: 147px;
	background-size: cover;
	margin-top: 49px;
}
#wert {
	/*margin-left: 25%;
	margin-right: 25%;*/
	text-align: center;
	font-size: 64px;
	padding-top: 10px;
	color: #964b82 !important;
}
#text_to {
	width: 100%;
	text-align: center;
	font-size: 24px;
	padding-top: 70px;
}
#barcode {
	position: fixed;
	bottom: -100px;
	right: 500px;
}
svg {
	-webkit-transform: rotate(270deg) !important;
	-moz-transform: rotate(270deg) !important;
	-ms-transform: rotate(270deg) !important;
	-o-transform: rotate(270deg) !important;
	transform: rotate(270deg) !important;
}
#widmung {
	width: 100%;
	max-width: 100%;
	text-align: center;
	font-size: 18px;
	padding-top: 100px;
	word-wrap: break-word;
}
#text_from{
	width: 100%;
	text-align: center;
	font-size: 24px;
	padding-top: 30px;
}
#beisatz{
	width: 100%;
	text-align: center;
	font-size: 11px;
	padding-top: 30px;
}
#fusszeile {
	width: 100%;
	text-align: center;
	padding-top: 120px;
}
#fusszeile_img {
	position: absolute;
	width: 55%;
	bottom: -160px;
	right: 155px;
}

html:
{% if doc.motiv == 'Wintergruss' %}
	{% set motiv = '/assets/gcm/images/herz.jpg' %}
{% endif %}
{% if doc.motiv == 'Herbstblüte' %}
	{% set motiv = '/assets/gcm/images/blumen.jpg' %}
{% endif %}
{% if doc.motiv == 'Glücksbringer' %}
	{% set motiv = '/assets/gcm/images/blumen_2.jpg' %}
{% endif %}
{% if doc.motiv == 'Sommergarten' %}
	{% set motiv = '/assets/gcm/images/hortensien.jpg' %}
{% endif %}
{% if doc.motiv == 'Frühlingsstrauss' %}
	{% set motiv = '/assets/gcm/images/tulpenstrauss.jpg' %}
{% endif %}
<img id="motiv_img" src="{{ motiv }}" style="width: 100%;">
<div id="gutschein_box">
	<div id="wert">
		Fr. {{ doc.amount}}.-
	</div>
	<div id="text_to">
		{% if doc.text_to %}Für {{ doc.text_to }} {% endif %}
	</div>
</div>
<div id="widmung">
	{% if doc.inscription %}{{ doc.inscription }}{% endif %}
</div>
<div id="text_from">
	{% if doc.text_from %}Von {{ doc.text_from }}{% endif %}
</div>
<div id="beisatz">
	<p>Dieser Gutschein ist einlösbar im<br>Garten-Center Meier, 8635 Dürnten bis zum {{ frappe.utils.get_datetime(doc.valid_date).strftime('%d.%m.%Y') }}</p>
</div>
<div id="fusszeile">
	<img id="fusszeile_img" src="/assets/gcm/images/Meier_Fusszeile.png">
</div>
<div id="barcode">
	{{ doc.barcode }}
</div>
